mapper.select:
SELECT 

FROM jc_farmland a

WHERE a.id = #{id}

SELECT
FROM jc_farmland a
a.del_flag =1
AND a.farmland_num LIKE '%'||#{farmlandNum}||'%' '%'+#{farmlandNum}+'%' concat('%',#{farmlandNum},'%') #{farmlandNum}
AND a.plant_variety LIKE '%'||#{plantVaritety}||'%' '%'+#{plantVaritety}+'%' concat('%',#{plantVaritety},'%')
AND a.user_id = #{user.id}
AND a.used_id = #{usedId}
AND a.farmer_id=#{farmer.id}
AND f.name LIKE '%'||#{farmer.name}||'%' '%'+#{farmer.name}+'%' concat('%',#{farmer.name},'%')
AND u.name LIKE '%'||#{usedName}||'%' '%'+#{usedName}+'%' concat('%',#{usedName},'%')
AND a.use_flag = #{useFlag}
ORDER BY ${page.orderBy}

mapper.update:
UPDATE jc_farmland SET
farmer_id =#{farmer.id},
realy_id =#{relay.id}
WHERE id = #{id}

UPDATE jc_farmland SET 
	del_flag = #{delFlag}
WHERE id = #{id}
